import consts from '../consts.js';

/**
 * This class implements Champion endpoints of the LoL API <https://developer.riotgames.com/api-methods/#champion-v3>
 * @author Filip Butkiewicz aka. bagnz0r <http://github.com/bagnz0r>
 */
class Champion {

    constructor(apiKey, region) {
        this.headers = new Headers({
            'X-Riot-Token': apiKey
        });

        this.url = `https://${consts.zone[region]}/lol/platform/v3`;
    }

    /**
     * Retrieve all champions.
     * @return {Promise}
     */
    getChampions() {
        return fetch(`${this.url}/champions`, { headers: this.headers });
    }

    /**
     * Get a champion by ID.
     * @param {Number} championId
     * @return {Promise}
     */
    getChampionById(championId) {
        return fetch(`${this.url}/champions/${championId}`, { headers: this.headers });
    }

}

export default Champion;
