import consts from '../consts.js';

/**
 * This class implements lol-status endpoints of the LoL API <https://developer.riotgames.com/api-methods/#lol-status-v3>
 * @author Filip Butkiewicz aka. bagnz0r <http://github.com/bagnz0r>
 */
class LolStatus {

    constructor(apiKey, region) {
        this.headers = new Headers({
            'X-Riot-Token': apiKey
        });
        
        this.url = `https://${consts.zone[region]}/lol/lol-status/v3`;
    }

    /**
     * Get League of Legends status for the given shard.
     * @return {Promise}
     */
    getShardData() {
        return fetch(`${this.url}/shard-data`, { headers: this.headers });
    }

}

export default LolStatus;
