import consts from '../consts.js';

/**
 * This class implements masteries endpoints of the LoL API <https://developer.riotgames.com/api-methods/#masteries-v3>
 * @author Filip Butkiewicz aka. bagnz0r <http://github.com/bagnz0r>
 */
class Masteries {

    constructor(apiKey, region, summonerId) {
        this.headers = new Headers({
            'X-Riot-Token': apiKey
        });

        this.summonerId = summonerId;
        this.url = `https://${consts.zone[region]}/lol/platform/v3`;
    }

    /**
     * Get mastery pages for a given summoner.
     * @return {Promise}
     */
    getMasteries() {
        return fetch(`${this.url}/masteries/by-summoner/${this.summonerId}`, { headers: this.headers });
    }

}

export default Masteries;
