import consts from '../consts.js';

/**
 * This class implements runes endpoints of the LoL API <https://developer.riotgames.com/api-methods/#runes-v3>
 * @author Filip Butkiewicz aka. bagnz0r <http://github.com/bagnz0r>
 */
class Runes {

    constructor(apiKey, region, summonerId) {
        this.headers = new Headers({
            'X-Riot-Token': apiKey
        });

        this.summonerId = summonerId;
        this.url = `https://${consts.zone[region]}/lol/platform/v3`;
    }

    /**
     * Get rune pages for a given summoner.
     * @return {Promise}
     */
    getRunes() {
        return fetch(`${this.url}/runes/by-summoner/${this.summonerId}`, { headers: this.headers });
    }

}

export default Runes;
