import consts from '../consts.js';

/**
 * This class implements spectator endpoints of the LoL API <https://developer.riotgames.com/api-methods/#spectator-v3>
 * @author Filip Butkiewicz aka. bagnz0r <http://github.com/bagnz0r>
 */
class Spectator {

    constructor(apiKey, region) {
        this.headers = new Headers({
            'X-Riot-Token': apiKey
        });

        this.url = `https://${consts.zone[region]}/lol/spectator/v3`;
    }

    /**
     * Get current game information for the given summoner ID.
     * @param {Number} summonerId
     * @return {Promise}
     */
    getCurrentGameBySummonerId(summonerId) {
        return fetch(`${this.url}/active-games/by-summoner/${summonerId}`, { headers: this.headers });
    }

    /**
     * Retrieves a list of featured games.
     * @return {Promise}
     */
    getFeaturedGames() {
        return fetch(`${this.url}/featured-games`, { headers: this.headers });
    }

}

export default Spectator;
